    this.draw = function( x, y ){  
        this.x = x;
        this.y = y;
          
        
        this.drawHead( );
        this.drawHairs();
        this.drawEars();
        this.drawUpperBody();
        this.drawBottoms();
        this.drawShirt();
        
        
        
        this.drawShort();
        this.drawHands();
        this.drawShoes();
        
        switch( this.face ) {
            case "great":
                this.drawHappyFace();
                break;
            case "so_so":
                this.drawNormalFace();
                break;
            case "tired":
                this.drawSadFace();
                break;
            default:
               this.drawNormalFace(); 
        }
        this.drawEyes();
        this.drawNose();
        
        //this.drawBrassard();
       // this.drawWatch();
    }
    
    
    
    
